import React, { useState } from 'react';
import axios from 'axios';

const SpoonacularSearch = () => {
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);

  const searchRecipes = async () => {
    try {
      const res = await axios.get(`https://api.spoonacular.com/recipes/complexSearch`, {
        params: {
          apiKey: process.env.REACT_APP_SPOONACULAR_API_KEY,
          query: query,
          number: 10
        }
      });
      setRecipes(res.data.results);
    } catch (err) {
      console.error('Error fetching recipes:', err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Search Recipes via Spoonacular</h2>
      <input
        type="text"
        placeholder="e.g. Pasta"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={searchRecipes}>Search</button>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '1rem', marginTop: '2rem' }}>
        {recipes.map(recipe => (
          <div key={recipe.id} style={{ border: '1px solid #ccc', padding: '1rem' }}>
            <h4>{recipe.title}</h4>
            <img src={recipe.image} alt={recipe.title} style={{ width: '100%' }} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default SpoonacularSearch;
