import React, { useState } from 'react';

const SpoonacularSearch = () => {
  const [query, setQuery] = useState('');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);

  const apiKey = ' 0e976a8fef72424c8d41f08b3180f723'; // Replace with your actual API key

  const searchRecipes = async () => {
    if (!query) return;
    setLoading(true);
    try {
      const response = await fetch(
        `https://api.spoonacular.com/recipes/complexSearch?query=${query}&number=10&apiKey=${apiKey}`
      );
      const data = await response.json();
      setRecipes(data.results || []);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
    setLoading(false);
  };

  return (
    <div className="spoonacular-container" style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Search Recipes (Spoonacular API)</h2>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter recipe name..."
        style={{ padding: '0.5rem', width: '250px' }}
      />
      <button onClick={searchRecipes} style={{ padding: '0.5rem 1rem', marginLeft: '10px' }}>
        Search
      </button>

      {loading && <p>Loading recipes...</p>}

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem', marginTop: '2rem' }}>
        {recipes.map((recipe) => (
          <div key={recipe.id} style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '1rem' }}>
            <h3>{recipe.title}</h3>
            <img src={recipe.image} alt={recipe.title} style={{ width: '100%', height: 'auto', borderRadius: '8px' }} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default SpoonacularSearch;
