import React, { useState } from "react";
import axios from "axios";

function Login({ setToken }) {
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({ username: "", password: "" });

  const handleSubmit = () => {
    axios
      .post(`http://localhost:5000/${isRegister ? "register" : "login"}`, form)
      .then((res) => {
        if (res.data.token) {
          localStorage.setItem("token", res.data.token);
          setToken(res.data.token);
        } else {
          alert("Success: Please login");
          setIsRegister(false);
        }
      })
      .catch(() => alert("Invalid credentials or user already exists"));
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h2>{isRegister ? "Register" : "Login"}</h2>
      <input
        placeholder="Username"
        onChange={(e) => setForm({ ...form, username: e.target.value })}
      />
      <input
        placeholder="Password"
        type="password"
        onChange={(e) => setForm({ ...form, password: e.target.value })}
      />
      <button onClick={handleSubmit}>{isRegister ? "Register" : "Login"}</button>
      <p onClick={() => setIsRegister(!isRegister)} style={{ cursor: "pointer", color: "blue" }}>
        {isRegister ? "Already have an account? Login" : "Don't have an account? Register"}
      </p>
    </div>
  );
}

export default Login;
