import React, { useEffect, useState } from "react";
import axios from "axios";
import Login from "./Login";
import SpoonacularSearch from './components/SpoonacularSearch';

<Route path="/spoonacular" element={<SpoonacularSearch />} />

function Home() {
  return (
    <div>
      <h1>Welcome to Recipe App</h1>
      <SpoonacularSearch />
    </div>
  );
}

function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [recipes, setRecipes] = useState([]);
  const [newRecipe, setNewRecipe] = useState({
    title: "",
    ingredients: "",
    instructions: "",
  });

  useEffect(() => {
    if (token) {
      axios
        .get("http://localhost:5000/recipes", {
          headers: { Authorization: token },
        })
        .then((res) => setRecipes(res.data));
    }
  }, [token]);

  const handleAdd = () => {
    axios
      .post("http://localhost:5000/recipes", newRecipe, {
        headers: { Authorization: token },
      })
      .then((res) => setRecipes([...recipes, res.data]));
  };

  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem("token");
  };

  if (!token) return <Login setToken={setToken} />;

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Recipe Sharing App</h2>
      <button onClick={handleLogout}>Logout</button>
      <h3>Add Recipe</h3>
      <input
        placeholder="Title"
        onChange={(e) => setNewRecipe({ ...newRecipe, title: e.target.value })}
      />
      <input
        placeholder="Ingredients"
        onChange={(e) =>
          setNewRecipe({ ...newRecipe, ingredients: e.target.value })
        }
      />
      <input
        placeholder="Instructions"
        onChange={(e) =>
          setNewRecipe({ ...newRecipe, instructions: e.target.value })
        }
      />
      <button onClick={handleAdd}>Add</button>

      <h3>Recipes</h3>
      <ul>
        {recipes.map((r, i) => (
          <li key={i}>
            <b>{r.title}</b>: {r.ingredients} - {r.instructions}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
